sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    //var vehStat = current.vehicle_status; // creates a variable for the current vehicle status field
    var glst = new GlideRecord('x_1119662_loaner_v_vehicle_servicing'); // creates a new GlideRecord object for the 'Vehicle Loaner Catalog' table.
    glst.addQuery('sys_id', current.vehicle); // adds a query condition for sys_id on the current vehicle tracker table
    glst.addQuery('tracker_status', 'open'); // adds a query condition for tracker_status to be open on servicing table
    //glst.query(); // executes through query

    if (!glst.query()) {
        glst.next(); // if there no matching records found from the previous query move on with the script
    }
    var newGlst = new GlideRecord('x_1119662_loaner_v_vehicle_servicing'); //Create a variable for a new GlideRecord on the servicing table
    var cgd = new GlideDate(); //Create a variable for a new GlideDate 
	//cgd.setValue(cgd.getValue()); //logs the value of the GlideDate object cgd to the ServiceNow system log. The getValue() method returns the date in the format yyyy-MM-dd.
    newGlst.initialize(); //Initialize the creation of a new record on the servicing table using the GlideRecord variable you created

    newGlst.setValue('vehicle_tracker', current.sys_id); //Set the value of vehicle tracker to the current records sys_id
    newGlst.setValue('vehicle', current.vehicle); //Set the value of vehicle to the current records vehicle value
    newGlst.setValue('vehicle_status', 'In Service'); //Set the value of vehicle status to in service
    newGlst.setValue('date_sent_for_servicing', cgd.getValue()); //Set the value of date sent for servicing to the current date
    newGlst.setValue('ticket_status', 'Open'); //Set the value of ticket status to open

	newGlst.insert(); 


})(current, previous);
