(function executeRule(current, previous /*null when async*/ ) {

    var vehStat = current.vehicle_status; // creates a variable for the current vehicle status field
    var grvt = new GlideRecord('x_1119662_loaner_v_lva_user'); // creates a new GlideRecord object for the 'Vehicle Loaner Catalog' table.
    grvt.addQuery('sys_id', current.vehicle); // adds a query condition for sys_id on the current vehicle table
    grvt.query(); // executes through query

     if (grvt.next()) { //Iterates through vehicle tracker table for true "if" statements

        if (vehStat == 'Pending Release' || vehStat == 'Out On Field' || vehStat == 'Returned For Inspection' || vehStat == 'Sent For Servicing') {
            grvt.status = 'Unavailable'; // If vehicle status is Pending Release, Out On Field, Returned For Inspection, or Sent For Servicing sets status  field on loaner vehicle to Unavailable.

        } else if (vehStat == 'Returned To Warehouse') {
            grvt.status = 'Available'; // If vehicle status on tracker table is Returned To Warehouse, sets status to Available.

        } else if (vehStat == 'Decommissioned') {
            grvt.status = 'Decommissioned'; // If vehicle status on tracker table is Decommissioned, sets status to Decommissioned.

        }

		grvt.update(); // updates the current record in the database

   /* if (grvt.next()) { // If a record is found, update the status based on the vehicle status

        switch (vehStat) {
            case 'Pending Release':
            case 'Out On Field':
            case 'Returned For Inspection':
            case 'Sent For Servicing':
                grvt.status == 'Unavailable';
                break;
            case 'Returned To Warehouse':
                grvt.status == 'Available';
                break;
            case 'Decommissioned':
                grvt.status == 'Decommissioned';
                break;
            default:
                // handle other cases 
                break;
        } // The switch statement iterates through each case in search of a match. If there is a match, the associated block of code is executed.
        grvt.update();*/
    }



})(current, previous);
